mapper.select:
select count(*) from t_user


		AND user_mobile = #{userMobile}


		and user_idcard = #{userIdcard}


		and user_id != #{userId}

mapper.insert:
insert into t_user (user_id, user_name, user_mobile,
  user_idcard, user_password, create_time, update_time,
  user_state
  )
values (#{userId,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR}, #{userMobile,jdbcType=VARCHAR},
   #{userIdcard,jdbcType=VARCHAR}, #{userPassword,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP},
   #{updateTime,jdbcType=TIMESTAMP}, #{userState,jdbcType=VARCHAR}
  )

mapper.update:
update t_user u,t_attach a
u.user_name = #{userName,jdbcType=VARCHAR}, u.user_mobile = #{userMobile,jdbcType=VARCHAR}, u.user_idcard = #{userIdcard,jdbcType=VARCHAR}, u.user_password = #{userPassword,jdbcType=VARCHAR}, u.create_time = #{createTime,jdbcType=TIMESTAMP}, u.update_time = #{updateTime,jdbcType=TIMESTAMP}, u.user_state = #{userState,jdbcType=VARCHAR}
where u.user_id = a.pkid AND u.user_id = #{userId,jdbcType=VARCHAR}

update t_user


		user_state = #{userState}


where user_id in

	#{s}
